import React from 'react';

function Login(){

    let [showUsername, setShowUsername] = React.useState('');
    
    async function getData(){
        const response = await fetch('/api/get')
        const data = await response.json()
        return data
    }
    
    async function postData(){
        const response = await fetch('/api/post', {
            method: "POST",
            body: JSON.stringify({
                text: 'text'
            }),
            headers: {
                'Content-type': 'application/json'
            }
        })
        const data = await response.text()
        console.log(data);
    }

    function usernameType(event){
        setShowUsername(<event className="target value"></event>)
    }

    return (
        <div className="container">
            <div className="m-auto text-center loginForm">
                <h2>Olá {showUsername}<span className='blink_me'>_</span></h2>
                <div className="input-group mb-2">
                <div className="input-group-prepend">
                    <span className="input-group-text" id="basic-addon1">@</span>
                </div>
                    <input onChange={usernameType} type="text" className="form-control" placeholder="Usuário" aria-label="Username" aria-describedby="basic-addon1"/>
                </div>
                <div className="input-group mb-2">
                <div className="input-group-prepend">
                    <span className="input-group-text" id="basic-addon1"><i class="icon fa-solid fa-key"></i></span>
                </div>
                    <input type="password" className="form-control" placeholder="Senha" aria-label="Username" aria-describedby="basic-addon1"/>
                </div>
                <button className="btn btn-lg btn-outline-secondary btn-block mb-2">Registrar</button>
                <br />
                <button className="btn btn-lg btn-outline-success btn-block">Entrar</button>
            </div>
        </div>
    )
}

export default Login
