import React from 'react';

function Login(){

    let [text, setText] = React.useState('Wait');
    
    async function getData(){
        const response = await fetch('/api/get')
        const data = await response.json()
        setText(data.data[0].username)
        return data
    }
    
    async function postData(){
        const response = await fetch('/api/post', {
            method: "POST",
            body: JSON.stringify({
                text: 'text'
            }),
            headers: {
                'Content-type': 'application/json'
            }
        })
        const data = await response.text()
        console.log(data);
    }

    return (
        <div class="container"><div class="m-auto text-center loginForm"><form><h2>Olá </h2><div class="input-group mb-2"><div class="input-group-prepend"><span class="input-group-text" id="basic-addon1">@</span></div><input type="text" class="form-control" placeholder="Usuário" aria-label="Username" aria-describedby="basic-addon1"/></div><div class="input-group mb-2"><div class="input-group-prepend"><span class="input-group-text" id="basic-addon1">&amp;</span></div><input type="password" class="form-control" placeholder="Senha" aria-label="Username" aria-describedby="basic-addon1"/></div><button class="btn btn-lg btn-outline-success btn-block">Entrar</button></form></div></div>
    )
}

export default Login
